          />
          <label style={{ marginLeft: 7 }}
            htmlFor='credit'
          >
            Credit
          </label>

          <input
            style={{ marginLeft: 40 }}
            type='radio'
            id='debit'
            name='debit'
            value='debit'
            checked={formData.TransactionType === 'debit'}
            onChange={(e) => setFormData({ ...formData, TransactionType: e.target.value })}
          />
          <label style={{ marginLeft: 7 }}
            htmlFor='debit'
          >
            Debit
          </label>
        </div>



