          onClick={() => {
            setFormData({ ...formData, MerchantID: Number(formData.MerchantID), NotAExpense: Number(formData.NotAExpense), BalanceAmount: Number(formData.BalanceAmount), DestinationCurrency: Number(formData.DestinationCurrency) });
